import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useHistory, useParams } from 'react-router-dom';

import { LIST_NAME } from '../ProjectType.constants';
import { getDetail } from '../../../services';

import { editProjectTypeDetailService } from '../ProjectType.services';

function EditProjecType  () {
  const { id } = useParams();
  const history = useHistory();
  const dispatch = useDispatch();

  const [pTypeName, setpTypeName] = useState('');
  const [pTypeDescription, setpTypeDescription] = useState('');


  const [pTypeStatus, setpTypeStatus] = useState({
    val: '',
  });
  const [pTypePriority, setpTypePriority] = useState({
    val: '',
  });

  const onNameChange = e => {
    const { value } = e.target;
    setpTypeName({ value });
  };

  const onDescriptionChange = e => {
    const { value } = e.target;
    setpTypeDescription({ value });
  };

  const onStatusChange = e => {
    const { value } = e.target;
    setpTypeStatus({ value });
  };
  const onPriorityChange = e => {
    const { value } = e.target;
    setpTypePriority({ value });
  };

  const clearInput = () => {
    const initState = {
      value: '',
    };
    setpTypeName(initState);
    setpTypeDescription(initState);
    setpTypeStatus(initState);
    setpTypePriority(initState);
  };

  const onSubmit = () => {
    const data = {
      name: pTypeName.val,
      description: pTypeDescription.val,
      status: pTypeStatus.val,
      id,
    };
    dispatch(editProjectTypeDetailService(data));
    clearInput();
    onReturn();
  };

  useEffect(() => {
    const detail = getDetail(LIST_NAME, id);
    console.log(detail);
    if (detail) {
      setpTypeName({ ...pTypeName, value: detail.name });
      setpTypeDescription({ ...pTypeDescription, value: detail.description });
      setpTypeStatus({ ...pTypeStatus, value: detail.status });
      setpTypeDescription({})
    }
  }, []);

  const onReturn = () => {
    history.goBack();
  };

  return (
    <div className='bg-white flex flex-col mt-10 rounded-xl shadow'>
      <div className=' border-b border-gray-bgTag flex '>
        <button onClick={onReturn} className='border-r-2 border-gray-300 pr-5'>
          <i className='fas fa-arrow-left text-xl text-black hover:text-gray-400 ml-5 '></i>
        </button>
        <span className=' text-xl font-bold p-5  '>Edit Project type:</span>
      </div>

      <div className='self-center m-5 rounded-xl shadow p-4'>
        <form onSubmit={onSubmit}>
          <label className='block mt-2'>
            <span className='block font-bold text-black'>Name: </span>
            <input
              className='block formInput'
              type='text'
              id='pTypeName'
              name='pTypeName '
              value={pTypeName.val}
              onChange={onNameChange}
            />
          </label>
          <label className='block mt-3'>
            <span className='block font-bold text-black'>Description:</span>
            <textarea
              className='block formInput'
              type='type'
              id='pTypeDescription'
              name='pTypeDescription'
              value={pTypeDescription.val}
              onChange={onDescriptionChange}
              rows='3'
            ></textarea>
          </label>
          <label className='block mt-3'>
            <div className='inline-block mr-5'>
              <span className='inline-block font-bold text-black'>Priority:</span>
              <select
                className=' inline-block formInput'
                name='pTypeDescription'
                value={pTypePriority.val}
                onChange={onPriorityChange}
              >
                <option value='' hidden>
                  Pick an option...
                </option>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
                <option value='5'>5</option>
              </select>
            </div>
            <div className='inline-block '>
              <span className='inline-block  font-bold text-black'>Status:</span>
              <select
                className='inline-block formInput'
                name='Status:'
                value={pTypeStatus.val}
                onChange={onStatusChange}
              >
                <option value='' hidden>
                  Pick an option...
                </option>
                <option value='active'>Active</option>
                <option value='inactive'>Inactive</option>
              </select>
            </div>
          </label>

          <div className='flex justify-center'>
            <button className='bg-green-700 hover:bg-green-800 text-white font-bold rounded-md px-4 py-2 mt-3'>
              Change
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EditProjecType
