import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useHistory, useParams } from "react-router-dom";

import { TABLE_NAME } from "../project-type.constants";
import { getDetail } from "services/api";
import { validateInput } from "utils/validateInput";
import { isValidSubmit } from "utils/submitForm";
import { editProjectTypeDetailSevice } from "../project-type.services";
import Button from "components/button";
import ButtonBack from "components/button-back";

export default function EditProjectStatus() {
  const { id } = useParams();
  const history = useHistory();
  const dispatch = useDispatch();
  const [name, setName] = useState({
    value: "",
    errorMessage: "",
    isInputValid: true,
  });
  const [description, setDescription] = useState({
    value: "",
    errorMessage: "",
    isInputValid: true,
  });
  const [status, setStatus] = useState({
    value: "",
    errorMessage: "",
    isInputValid: true,
  });

  const handleChangeName = e => {
    const { name, value } = e.target;
    const { isInputValid, errorMessage } = validateInput(name, value);
    setName({ value, errorMessage, isInputValid });
  };

  const handleChangeDescription = e => {
    const { name, value } = e.target;
    const { isInputValid, errorMessage } = validateInput(name, value);
    setDescription({ value, errorMessage, isInputValid });
  };

  const handleChangeStatus = e => {
    const { name, value } = e.target;
    const { isInputValid, errorMessage } = validateInput(name, value);
    setStatus({ value, errorMessage, isInputValid });
  };

  const checkForm = () => {
    const validName = validateInput("name", name.value);
    const validDescription = validateInput("description", description.value);
    const validStatus = validateInput("status", status.value);
    setName({
      value: name.value.trim(),
      errorMessage: validName.errorMessage,
      isInputValid: validName.isInputValid,
    });
    setDescription({
      value: description.value.trim(),
      errorMessage: validDescription.errorMessage,
      isInputValid: validDescription.isInputValid,
    });
    setStatus({
      ...status,
      errorMessage: validStatus.errorMessage,
      isInputValid: validStatus.isInputValid,
    });
  };

  const clearInput = () => {
    const initState = {
      value: "",
      errorMessage: "",
      isInputValid: true,
    };
    setName(initState);
    setDescription(initState);
    setStatus(initState);
  };

  const handleSubmit = () => {
    checkForm();
    const formData = [name, description, status];
    if (isValidSubmit(formData)) {
      const data = {
        name: name.value,
        description: description.value,
        status: status.value,
        id,
      };
      dispatch(editProjectTypeDetailSevice(data));
      clearInput();
      handleBack();
    }
  };

  useEffect(() => {
    const detail = getDetail(TABLE_NAME, id);
    if (detail) {
      setName({ ...name, value: detail.name });
      setDescription({ ...description, value: detail.description });
      setStatus({ ...status, value: detail.status });
    }
  }, []);

  const handleBack = () => {
    history.goBack();
  };


  return (
    <div className='bg-white flex flex-col mt-10 rounded-xl shadow'>
      <div className=' border-b border-gray-bgTag flex '>
        <button onClick={onReturn} className='border-r-2 border-gray-300 pr-5'>
          <i className='fas fa-arrow-left text-xl text-black hover:text-gray-400 ml-5 '></i>
        </button>
        <span className=' text-xl font-bold p-5  '>Edit Project type:</span>
      </div>

      <div className='self-center m-5 rounded-xl shadow p-4'>
        <form onSubmit={onSubmit}>
          <label className='block mt-2'>
            <span className='block font-bold text-black'>Name: </span>
            <input
              className='block formInput'
              type='text'
              id='pTypeName'
              name='pTypeName '
              value={pTypeName.val}
              onChange={onNameChange}
            />
          </label>
          <label className='block mt-3'>
            <span className='block font-bold text-black'>Description:</span>
            <textarea
              className='block formInput'
              type='type'
              id='pTypeDescription'
              name='pTypeDescription'
              value={pTypeDescription.val}
              onChange={onDescriptionChange}
              rows='3'
            ></textarea>
          </label>
          <label className='block mt-3'>
            <div className='inline-block mr-5'>
              <span className='inline-block font-bold text-black'>Priority:</span>
              <select
                className=' inline-block formInput'
                name='pTypeDescription'
                value={pTypePriority.val}
                onChange={onPriorityChange}
              >
                <option value='' hidden>
                  Pick an option...
                </option>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
                <option value='5'>5</option>
              </select>
            </div>
            <div className='inline-block '>
              <span className='inline-block  font-bold text-black'>Status:</span>
              <select
                className='inline-block formInput'
                name='Status:'
                value={pTypeStatus.val}
                onChange={onStatusChange}
              >
                <option value='' hidden>
                  Pick an option...
                </option>
                <option value='active'>Active</option>
                <option value='inactive'>Inactive</option>
              </select>
            </div>
          </label>

          <div className='flex justify-center'>
            <button className='bg-green-700 hover:bg-green-800 text-white font-bold rounded-md px-4 py-2 mt-3'>
              Change
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
